import React, { useState } from 'react';
import Sidebar from '../../components/Sidebar';
import WelcomeHeader from '../../components/WelcomeHeader';
import WhiteRectangle from '../../components/WhiteRectangle';
import NotificationBell from '../../components/NotificationBell';
import ProductModal from '../../components/ProductModal/ProductModal';
import penIcon from '../../assets/images/pen.svg';
import trashIcon from '../../assets/images/trash.svg';
import cameraIcon from '../../assets/images/Camera.svg';
import '../../styles/shared.css';
import './product.css';
// 移除所有其他不再需要的导入

const Product = () => {
  const [products, setProducts] = useState([
    {
      id: '001',
      name: 'Cocacola',
      barcode: '1234567891078',
      price: '$3.99',
      stock: '50'
    }
  ]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingCell, setEditingCell] = useState(null);

  const handleAddProduct = () => {
    setIsModalOpen(true);
  };

  const handleDeleteProduct = (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      setProducts(products.filter(product => product.id !== productId));
    }
  };

  const handleModalSubmit = (productData) => {
    setProducts([...products, productData]);
  };

  const handleCellEdit = (productId, field) => {
    setEditingCell({ productId, field });
  };

  const handleCellChange = (e, productId, field) => {
    const value = e.target.value;
    setProducts(products.map(product => {
      if (product.id === productId) {
        if (field === 'price') {
          // 确保价格格式正确
          const numericValue = value.replace(/[^0-9.]/g, '');
          return { ...product, [field]: `$${numericValue}` };
        }
        return { ...product, [field]: value };
      }
      return product;
    }));
  };

  const handleCellBlur = () => {
    setEditingCell(null);
  };

  const handleCellKeyDown = (e, productId, field) => {
    if (e.key === 'Enter') {
      setEditingCell(null);
    }
  };

  const handleCameraClick = () => {
    // 跳转到摄像头页面
    window.location.href = '/camera';
  };

  const renderCell = (product, field) => {
    const isEditing = editingCell?.productId === product.id && editingCell?.field === field;
    
    if (isEditing) {
      return (
        <div className="cell-with-icon">
          <input
            type={field === 'price' || field === 'stock' ? 'number' : 'text'}
            value={field === 'price' ? product[field].replace('$', '') : product[field]}
            onChange={(e) => handleCellChange(e, product.id, field)}
            onBlur={handleCellBlur}
            onKeyDown={(e) => handleCellKeyDown(e, product.id, field)}
            className="cell-input"
            autoFocus
            min={field === 'price' || field === 'stock' ? '0' : undefined}
            step={field === 'price' ? '0.01' : '1'}
          />
          {field === 'barcode' && (
            <img
              src={cameraIcon}
              alt="Scan Barcode"
              className="camera-icon"
              onClick={handleCameraClick}
            />
          )}
        </div>
      );
    }

    return (
      <div className="cell-with-icon">
        <div 
          className="editable-cell"
          onClick={() => handleCellEdit(product.id, field)}
        >
          {product[field]}
        </div>
        {field === 'barcode' && (
          <img
            src={cameraIcon}
            alt="Scan Barcode"
            className="camera-icon"
            onClick={handleCameraClick}
          />
        )}
      </div>
    );
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-main">
        <NotificationBell />
        <div className="product-welcome-container">
          <WelcomeHeader />
        </div>
        <div className="product-button-container">
          <button className="add-item-button" onClick={handleAddProduct}>
            Add items+
          </button>
        </div>
        <div className="product-rectangle-wrapper">
          <WhiteRectangle title="Product List">
            <div className="product-labels-row">
              <div className="product-label">No.</div>
              <div className="product-label">Product Name</div>
              <div className="product-label">Bar Code</div>
              <div className="product-label">Price</div>
              <div className="product-label">Stock</div>
              <div className="product-label">Action</div>
            </div>
            <div className="product-separator"></div>
            {products.map((product) => (
              <div key={product.id} className="product-data-row">
                <div className="product-data-cell">{product.id}</div>
                <div className="product-data-cell">
                  {renderCell(product, 'name')}
                </div>
                <div className="product-data-cell">
                  {renderCell(product, 'barcode')}
                </div>
                <div className="product-data-cell">
                  {renderCell(product, 'price')}
                </div>
                <div className="product-data-cell">
                  {renderCell(product, 'stock')}
                </div>
                <div className="product-data-cell">
                  <div className="product-actions">
                    <img
                      src={trashIcon}
                      alt="Delete"
                      className="product-action-icon"
                      onClick={() => handleDeleteProduct(product.id)}
                    />
                  </div>
                </div>
              </div>
            ))}
          </WhiteRectangle>
        </div>
      </div>

      <ProductModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={handleModalSubmit}
      />
    </div>
  );
};

export default Product;
