import React, { useState } from 'react';
import bellIcon from '../assets/images/bell.svg';
import './NotificationBell.css';

const NotificationBell = ({ lowStockProducts, hasLowStock }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNotifications = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="notification-container">
      <div className="notification-bell" onClick={toggleNotifications}>
        <img 
          src={bellIcon} 
          alt="Notifications" 
          className={`bell-icon ${hasLowStock ? 'bell-icon-red' : ''}`}
        />
        {hasLowStock && <div className="notification-dot"></div>}
      </div>
      
      {isOpen && (
        <div className="notification-popup">
          <div className="notification-header">
            <h3>库存提醒</h3>
          </div>
          <div className="notification-content">
            {lowStockProducts.length > 0 ? (
              lowStockProducts.map(product => (
                <div key={product.id} className="notification-item">
                  <div className="notification-item-name">{product.name}</div>
                  <div className="notification-item-stock">
                    当前库存: {product.stock}
                    <span className="stock-warning"> (需要补货)</span>
                  </div>
                </div>
              ))
            ) : (
              <div className="notification-empty">没有需要补货的商品</div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default NotificationBell;
