import React, { useState } from 'react';
import Sidebar from '../../components/Sidebar';
import WelcomeHeader from '../../components/WelcomeHeader';
import WhiteRectangle from '../../components/WhiteRectangle';
// import NotificationBell from '../../components/NotificationBell';
import '../../styles/shared.css';
import './user.css';

const User = () => {
  const [users, setUsers] = useState([
    {
      no: '001',
      name: 'Tom Lee',
      birthday: '08/08/2000',
      career: 'Student',
      email: '12345@qq.com',
      rewards: '1000'
    }
  ]);
  const [editingCell, setEditingCell] = useState(null);

  const handleCellClick = (userId, field) => {
    setEditingCell({ id: userId, field });
  };

  const handleCellChange = (e, userId, field) => {
    const value = e.target.value;
    setUsers(users.map(user => {
      if (user.no === userId) {
        return { ...user, [field]: value };
      }
      return user;
    }));
  };

  const handleInputBlur = () => {
    setEditingCell(null);
  };

  const handleInputKeyDown = (e) => {
    if (e.key === 'Enter') {
      setEditingCell(null);
    }
  };

  const renderCell = (user, field) => {
    const isEditing = editingCell?.id === user.no && editingCell?.field === field;

    if (isEditing) {
      return (
        <input
          type={field === 'rewards' ? 'number' : 'text'}
          value={user[field]}
          onChange={(e) => handleCellChange(e, user.no, field)}
          onBlur={handleInputBlur}
          onKeyDown={handleInputKeyDown}
          className="cell-input"
          autoFocus
          min={field === 'rewards' ? '0' : undefined}
        />
      );
    }

    return (
      <div 
        className="user-data-cell editable-cell"
        onClick={() => handleCellClick(user.no, field)}
      >
        {user[field]}
      </div>
    );
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-main">
        {/* <NotificationBell /> */}
        <div className="user-welcome-container">
          <WelcomeHeader />
        </div>
        <div className="user-rectangle-wrapper">
          <WhiteRectangle title="User List">
            <div className="user-labels-row">
              <div className="user-label">No.</div>
              <div className="user-label">Name</div>
              <div className="user-label">Birthday</div>
              <div className="user-label">Career</div>
              <div className="user-label">Email</div>
              <div className="user-label">Rewards</div>
            </div>
            <div className="user-separator"></div>
            {users
              .slice() // 创建一个副本以避免直接修改state
              .sort((a, b) => a.no.localeCompare(b.no)) // 按用户编号升序排序
              .map((user, index) => (
                <div key={index} className="user-data-row">
                  <div className="user-data-cell">{user.no}</div>
                  {renderCell(user, 'name')}
                  {renderCell(user, 'birthday')}
                  {renderCell(user, 'career')}
                  {renderCell(user, 'email')}
                  {renderCell(user, 'rewards')}
                </div>
              ))}
          </WhiteRectangle>
        </div>
      </div>
    </div>
  );
};

export default User;
