import React, { useState } from 'react';
import Sidebar from '../../components/Sidebar';
import WelcomeHeader from '../../components/WelcomeHeader';
import WhiteRectangle from '../../components/WhiteRectangle';
import NotificationBell from '../../components/NotificationBell';
import ProductModal from '../../components/ProductModal/ProductModal';
import penIcon from '../../assets/images/pen.svg';
import trashIcon from '../../assets/images/trash.svg';
import '../../styles/shared.css';
import './product.css';
// 移除所有其他不再需要的导入

const Product = () => {
  const [products, setProducts] = useState([
    {
      id: '001',
      name: 'Cocacola',
      barcode: '1234567891078',
      price: '$3.99',
      stock: '50'
    }
  ]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  const handleAddProduct = () => {
    setEditingProduct(null);
    setIsModalOpen(true);
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setIsModalOpen(true);
  };

  const handleDeleteProduct = (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      setProducts(products.filter(product => product.id !== productId));
    }
  };

  const handleModalSubmit = (productData) => {
    if (editingProduct) {
      // 编辑现有产品
      setProducts(products.map(product => 
        product.id === editingProduct.id ? productData : product
      ));
    } else {
      // 添加新产品
      setProducts([...products, productData]);
    }
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="dashboard-main">
        <NotificationBell />
        <div className="product-welcome-container">
          <WelcomeHeader />
        </div>
        <div className="product-button-container">
          <button className="add-item-button" onClick={handleAddProduct}>
            Add items+
          </button>
        </div>
        <div className="product-rectangle-wrapper">
          <WhiteRectangle title="Product List">
            <div className="product-labels-row">
              <div className="product-label">No.</div>
              <div className="product-label">Product Name</div>
              <div className="product-label">Bar Code</div>
              <div className="product-label">Price</div>
              <div className="product-label">Stock</div>
              <div className="product-label">Action</div>
            </div>
            <div className="product-separator"></div>
            {products.map((product, index) => (
              <div key={product.id} className="product-data-row">
                <div className="product-data-cell">{product.id}</div>
                <div className="product-data-cell">{product.name}</div>
                <div className="product-data-cell">{product.barcode}</div>
                <div className="product-data-cell">{product.price}</div>
                <div className="product-data-cell">{product.stock}</div>
                <div className="product-data-cell">
                  <div className="product-actions">
                    <img
                      src={penIcon}
                      alt="Edit"
                      className="product-action-icon"
                      onClick={() => handleEditProduct(product)}
                    />
                    <img
                      src={trashIcon}
                      alt="Delete"
                      className="product-action-icon"
                      onClick={() => handleDeleteProduct(product.id)}
                    />
                  </div>
                </div>
              </div>
            ))}
          </WhiteRectangle>
        </div>
      </div>

      <ProductModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={handleModalSubmit}
        product={editingProduct}
      />
    </div>
  );
};

export default Product;
